const modalBtn=document.querySelectorAll("[data-modal]"),modalClose=document.querySelectorAll(".modal-close"),modal=document.querySelectorAll(".modal"),body=document.body;function closeModal(e){let t=e.querySelector(".modal__content");t.removeAttribute("style"),setTimeout(()=>{e.classList.remove("show"),body.classList.remove("scroll-off")},200)}modalBtn.forEach(e=>{e.addEventListener("click",e=>{let t=e.currentTarget;e=t.getAttribute("data-modal");let o=document.getElementById(e),l=o.querySelector(".modal__content");l.addEventListener("click",e=>{e.stopPropagation()}),o.classList.add("show"),body.classList.add("scroll-off"),setTimeout(()=>{l.style.opacity="1",l.style.transform="none"},1)})}),modalClose.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget.closest(".modal"))})}),modal.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget)})});